import { Timeout } from './timeout';
/**
 * A task in the queue that is waiting for a connection
 * to become available.
 */
export default class Waiter<T> {
    static waiterTimeout<T>(waiter: Waiter<T>): void;
    private _resolve;
    private readonly _timeout;
    constructor(resolve: (result: Promise<T> | T | Timeout) => unknown, timeoutMilliseconds: number);
    isTimedOut(): boolean;
    resolve(connection: T | Promise<T>): void;
}
