export declare const timeout: unique symbol;
export declare type Timeout = typeof timeout;
export declare function isTimeout<T>(value: T | Timeout): value is Timeout;
export declare function isNotTimeout<T>(value: T | Timeout): value is T;
export declare function withTimeout<TArgs extends any[], TResult>(fn: (...args: TArgs) => Promise<TResult> | TResult, { timeoutMilliseconds, onResultAfterTimeout, }: {
    timeoutMilliseconds: number;
    onResultAfterTimeout?: (result: TResult) => void;
}, ...args: TArgs): Promise<TResult | Timeout>;
